import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CourseServeice from "../services/course.service";

const CourseComponent = (props) => {
  let navigate = useNavigate();
  let { currentUser, setCurrentUser } = props;
  let [coursedata, setCoursedata] = useState(null);
  const handlelogin = function () {
    navigate("/login");
  };
  useEffect(() => {
    let _id;
    if (currentUser) {
      _id = currentUser.user._id;
    } else {
      _id = "";
    }
    CourseServeice.get(_id)
      .then((course) => {
        console.log(course);
        setCoursedata(course.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    <div style={{ padding: "3rem" }}>
      {!currentUser && (
        <div>
          <h1>You must login before accessing this page</h1>
          <button className="btn btn-primary btn-lg" onClick={handlelogin}>
            Click button to login
          </button>
        </div>
      )}
      {currentUser && currentUser.user.role === "instructor" && (
        <div>
          <h1>WELCOME TO INSTRUCTORS' COURSE PAGE</h1>
        </div>
      )}
      {currentUser && coursedata && coursedata.length != 0 && (
        <div>
          <p>Here are all the courses available in the system.</p>
          {coursedata.map((course) => (
            <div className="card" style={{ width: "18rem" }}>
              <div className="card-body">
                <h5 className="card-title">{course.title}</h5>
                <p className="card-text">{course.description}</p>
                <button className="btn btn-primary">{course.price}</button>
                <br />
              </div>
            </div>
          ))}
        </div>
      )}
      {currentUser && currentUser.user.role === "student" && (
        <div>
          <h1>WELCOME TO STUDENTS' COURSE PAGE</h1>
        </div>
      )}
    </div>
  );
};

export default CourseComponent;
